import React, {useState} from 'react';

import './App.css';

function App() {

  const [input, setInput] = useState('');
  return (
    <div className="App">
     <h1>Hello everyone.</h1>
     <input value={input} onChange={event => setInput(event.target.value)}/>
     <button>Send message</button>
    </div>
  );
}

export default App;
